`timescale 1 ns / 1 ns

module swap_counter
          (clk,
           reset_x,
           enb,
           In1,
           In2,
           Out1);


  input   clk;
  input   reset_x;
  input   enb;
  input   In1;
  input   In2;
  output  [3:0] Out1;  // ufix4


  wire switch_compare_1;
  wire [3:0] Switch_out1;  // ufix4
  wire switch_compare_1_1;
  reg [3:0] Delay_out1;  // ufix4
  wire [3:0] Switch8_out1;  // ufix4
  wire [3:0] Add_out1;  // ufix4


  assign switch_compare_1 = In1 > 1'b0;



  assign Switch_out1 = (switch_compare_1 == 1'b0 ? 4'b0000 :
              4'b0001);



  assign switch_compare_1_1 = In2 > 1'b0;



  assign Switch8_out1 = (switch_compare_1_1 == 1'b0 ? Delay_out1 :
              4'b0000);



  assign Add_out1 = Switch_out1 + Switch8_out1;



  always @(posedge clk)
    begin : Delay_process
      if (reset_x == 1'b0) begin
        Delay_out1 <= 4'b0000;
      end
      else begin
        if (enb) begin
          Delay_out1 <= Add_out1;
        end
      end
    end



  assign Out1 = Delay_out1;

endmodule  // swap_counter

